//================================== Auto Kill / Client Login ==================================//
setTimeout(() => {
  if (!client || !client.user) {
    console.log("Client Not Login, Process Kill");
    process.kill(1);
  } else {
    console.log("Client Login");
  }
}, 3 * 1000 * 60);

setTimeout(() => {
  process.kill(1);
  console.log("Client Login");
}, 22 * 10000 * 60);

client.login(config.token || process.env.token).catch((err) => {
  console.log(err.message);
});
